key_management.tf:
##############################################################################
# Key Management Instance Slz Kms
##############################################################################

resource "ibm_resource_instance" "slz_kms" {
  name              = "slz-slz-kms"
  resource_group_id = ibm_resource_group.slz_service_rg.id
  service           = "kms"
  plan              = "tiered-pricing"
  location          = "us-south"
  tags              = ["slz","landing-zone"]
}

resource "ibm_iam_authorization_policy" "slz_kms_server_protect_policy" {
  source_service_name         = "server-protect"
  target_service_name         = "kms"
  target_resource_instance_id = ibm_resource_instance.slz_kms.guid
  roles                       = ["Reader"]
  description                 = "Allow block storage volumes to be encrypted by Key Management instance."
}

resource "ibm_iam_authorization_policy" "slz_kms_block_storage_policy" {
  source_service_name         = "is"
  source_resource_type        = "share"
  target_service_name         = "kms"
  target_resource_instance_id = ibm_resource_instance.slz_kms.guid
  roles                       = ["Reader", "Authorization Delegator"]
  description                 = "Allow block storage volumes to be encrypted by Key Management instance."
}

resource "ibm_kms_key_rings" "slz_kms_slz_slz_ring_ring" {
  key_ring_id = "slz-slz-kms-slz-slz-ring"
  instance_id = ibm_resource_instance.slz_kms.guid
}

resource "ibm_kms_key" "slz_kms_slz_slz_key_key" {
  instance_id   = ibm_resource_instance.slz_kms.guid
  key_name      = "slz-slz-kms-slz-slz-key"
  standard_key  = false
  key_ring_id   = ibm_kms_key_rings.slz_kms_slz_slz_ring_ring.key_ring_id
  force_delete  = true
  endpoint_type = "public"
  depends_on = [
    ibm_iam_authorization_policy.slz_kms_server_protect_policy,
    ibm_iam_authorization_policy.slz_kms_block_storage_policy
  ]
}

resource "ibm_kms_key_policies" "slz_kms_slz_slz_key_key_policy" {
  instance_id   = ibm_resource_instance.slz_kms.guid
  endpoint_type = "public"
  key_id        = ibm_kms_key.slz_kms_slz_slz_key_key.key_id
  rotation {
    interval_month = 12
  }
  dual_auth_delete {
    enabled = false
  }
}

resource "ibm_kms_key" "slz_kms_slz_atracker_key_key" {
  instance_id   = ibm_resource_instance.slz_kms.guid
  key_name      = "slz-slz-kms-slz-atracker-key"
  standard_key  = false
  key_ring_id   = ibm_kms_key_rings.slz_kms_slz_slz_ring_ring.key_ring_id
  force_delete  = true
  endpoint_type = "public"
  depends_on = [
    ibm_iam_authorization_policy.slz_kms_server_protect_policy,
    ibm_iam_authorization_policy.slz_kms_block_storage_policy
  ]
}

resource "ibm_kms_key_policies" "slz_kms_slz_atracker_key_key_policy" {
  instance_id   = ibm_resource_instance.slz_kms.guid
  endpoint_type = "public"
  key_id        = ibm_kms_key.slz_kms_slz_atracker_key_key.key_id
  rotation {
    interval_month = 12
  }
  dual_auth_delete {
    enabled = false
  }
}

resource "ibm_kms_key" "slz_kms_slz_vsi_volume_key_key" {
  instance_id   = ibm_resource_instance.slz_kms.guid
  key_name      = "slz-slz-kms-slz-vsi-volume-key"
